import { useEffect, useState } from "react";

import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import Cart from "./components/Cart/Cart";
import CartProvider from "./store/CartProvider";
import DUMMY_MEALS from "./components/Meals/dummy-meals";

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const retrieveMeals = async () => {
    const response = await fetch(
      "https://taskapp-9191a-default-rtdb.europe-west1.firebasedatabase.app/meals/-NZPYFJ04Je_dPvFEM7f.json"
    );
    const data = await response.json();

    const loadedMeals = [];

    for (const key in data) {
      loadedMeals.push({
        id: key,
        name: data[key].name,
        price: data[key].price,
        description: data[key].description,
      });
    }
    console.log(loadedMeals);
  };

  useEffect(() => {
    retrieveMeals();
  }, []);

  // const uploadData = async () => {
  //   await fetch(
  //     "https://taskapp-9191a-default-rtdb.europe-west1.firebasedatabase.app/meals.json",
  //     {
  //       method: "POST",
  //       body: JSON.stringify(DUMMY_MEALS),
  //     }
  //   );
  // };

  // uploadData();

  return (
    <CartProvider>
      <Header onShowCart={showCartHandler} />
      {cartIsShown && <Cart onHideCart={hideCartHandler} />}
      <main>
        <Meals />
      </main>
    </CartProvider>
  );
}

export default App;
